business_objects.action:
function (db) {
    if(this.minor_version && this.package_file) {
        var currentVersion = (this.major_version || 0)+'.'+this.minor_version;
        var attachment = this.package_file;
        db.HostedPackage.findOne({'business_object_package._id':this._id}).then(function(hp) {
            if(!hp) {
                return;
            }

            for(var i=0; i < hp.available_versions.length; i++) {
                if(hp.available_versions[i] == currentVersion) {
                    return;
                }
            }
            hp.package_files.push(attachment);
            hp.available_versions.push(currentVersion);
            hp.save();
        });
    }
}

business_objects.function:
function (db, queryParams, res) {
    var GridFsService = db._svc.GridFsService;

    var key=queryParams.key;
    var version=queryParams.version;

    if(!key || !version) {
        throw 'missing required parameters';
    }

    return db.HostedPackage.findOne({"package_key":key})
    .then(
        function(hp){
            var pkgIndex=0;
            var versions = hp.available_versions;

            for(pkgIndex=0; pkgIndex < versions.length; pkgIndex++) {
                if(versions[pkgIndex] == version) break;
            }
            if(pkgIndex >= versions.length) {
                throw 'version '+version+' not found';
            }

            var fileId = hp.package_files[pkgIndex].attachment_id;

            return GridFsService.getFile(fileId);
        }
    )
    .then(
        function(f) {
            // console.log(f);
            return {
                __stream_response:f.readstream
            }
        }
    );

}

function (db, _) {
    return db.HostedPackage.find({}).then(function(hpList){
        var result = [];
        _.forEach(hpList, function(hp) {
            var versions = hp.available_versions;
            result.push({
               key:hp.package_key,
               latest_version:versions[versions.length-1],
               available_versions: versions
            });
        });
        return result;
    });
}
